mapper.update:
UPDATE OLLE_SUGGEST
SET SUG_KATEGORIE=#{sug_kategorie}, SUG_TITLE=#{sug_title}, SUG_CONTENT=#{sug_content}, SUG_WRITER=#{sug_writer}, SUG_TENDENCY=#{sug_tendency}, SUG_ADDR=#{sug_addr}, SUG_TERM=#{sug_term}
WHERE SUG_NUM=#{sug_num}

mapper.select:
SELECT COUNT(*)
FROM OLLE_SUGGEST
WHERE SUG_KATEGORIE=#{kategorie}
